        type:curType,
        payload :curstate

    }
}


function sortCount(curstate){
    return{
        type:"SORTCOUNT",
        payload :curstate

    }
}


function sortTitle(curstate){
    return{
        type:"SORTTITLE",
        payload :curstate

    }
}

