import Homepage from './containers/Homepage';
import Header from './components/Header';
import BlockList from './containers/BlockList';
import store from './Redux/store';
import { getConst } from './constants';





import './App.css';

function App() {
  useEffect(()=>{
    store.subscribe(()=>{
