import movieListTemp from "./constants"
import sortByAvg from "./function"

const reducer=(prevState={

    // this object will replace with fetch data later
    movieList:movieListTemp,
    actionType:"",
    curState:true

}, action)=>{

    
    let newState={...prevState}
    newState.curState=!action.payload
    newState.actionType=action.type
    
    switch(action.type){
        case "SORTAVG":
            let temp = sortByAvg(newState.movieList,action.payload)
            newState.movieList=temp      
            return newState
        
        case "DELETENUM":
            return prevState

        default:
            return prevState

    }



}

export default reducer
