import store from './store';
import sortAvg from './actionCreator';

export default function ListMove(props){
    
    //Watch the status of sort status (asc or desc), default is sort by asc
    const [sortNameAsc,setSortNameAsc] = useState(true)
    const [sortDateAsc,setSortDateAsc] = useState(true)
    const [sortCountAsc,setSortCountAsc] = useState(true)
    const [sortAvgAsc,setSortAvgAsc] = useState(true)

    useEffect(()=>{
        store.subscribe(()=>{
            // let temp = store.getState().curState
            // console.log(store.getState().curState)
            setSortAvgAsc(store.getState().curState)
            console.log(store.getState().actionType)

            

        })

    },[])

